enum MachineNodeRole {
        /**
         * Master-only node.
         *
         * <p>Heap is computed as 60% of total system memory up to a maximum of 31 gigabytes.
         */
        MASTER_ONLY(m -> mb(min((long) (m * .6), MAX_HEAP_SIZE))),

        /**
         * Machine learning only node.
         *
         * <p>Heap is computed as:
         * <ul>
         *     <li>40% of total system memory when total system memory 16 gigabytes or less.</li>
         *     <li>40% of the first 16 gigabytes plus 10% of memory above that when total system memory is more than 16 gigabytes.</li>
         *     <li>The absolute maximum heap size is 31 gigabytes.</li>
         * </ul>
         *
         * In all cases the result is rounded down to the next whole multiple of 4 megabytes.
         * The reason for doing this is that Java will round requested heap sizes to a multiple
         * of 4 megabytes (certainly versions 11 to 18 do this), so by doing this ourselves we
         * are more likely to actually get the amount we request. This is worthwhile for ML where
         * the ML autoscaling code needs to be able to calculate the JVM size for different sizes
         * of ML node, and if Java is also rounding then this causes a discrepancy. It's possible
         * that a future version of Java could round to an even bigger number of megabytes, which
         * would cause a discrepancy for people using that version of Java. But there's no harm
         * in a bit of extra rounding here - it can only reduce discrepancies.
         *
         * If this formula is changed then corresponding changes must be made to the {@code NativeMemoryCalculator} and
         * {@code MlAutoscalingDeciderServiceTests} classes in the ML plugin code. Failure to keep the logic synchronized
         * could result in repeated autoscaling up and down.
         */
        ML_ONLY(m -> mb(m <= (GB * 16) ? (long) (m * .4) : (long) min((GB * 16) * .4 + (m - GB * 16) * .1, MAX_HEAP_SIZE), 4)),

        /**
         * Data node. Essentially any node that isn't a master or ML only node.
         *
         * <p>Heap is computed as:
         * <ul>
         *     <li>40% of total system memory when less than 1 gigabyte with a minimum of 128 megabytes.</li>
         *     <li>50% of total system memory when greater than 1 gigabyte up to a maximum of 31 gigabytes.</li>
         * </ul>
         */
        DATA(m -> mb(m < GB ? max((long) (m * .4), MIN_HEAP_SIZE) : min((long) (m * .5), MAX_HEAP_SIZE))),

        /**
         * Unknown role node.
         *
         * <p>Hard-code heap to a default of 1 gigabyte.
         */
        UNKNOWN(m -> DEFAULT_HEAP_SIZE_MB);

        private final Function<Long, Integer> formula;

        MachineNodeRole(Function<Long, Integer> formula) {
            this.formula = formula;
        }

        /**
         * Determine the appropriate heap size for the given role and available system memory.
         *
         * @param systemMemory total available system memory in bytes
         * @return recommended heap size in megabytes
         */
        public int heap(long systemMemory) {
            return formula.apply(systemMemory);
        }

        private static int mb(long bytes) {
            return (int) (bytes / (1024 * 1024));
        }

        private static int mb(long bytes, int toLowerMultipleOfMb) {
            return toLowerMultipleOfMb * (int) (bytes / (1024 * 1024 * toLowerMultipleOfMb));
        }
    }

public final class JdkVectorLibrary implements VectorLibrary {

    static {
        System.loadLibrary("vec");
    }

    public JdkVectorLibrary() {}

    static final MethodHandle dot8stride$mh = downcallHandle("dot8s_stride", FunctionDescriptor.of(JAVA_INT));
    static final MethodHandle sqr8stride$mh = downcallHandle("sqr8s_stride", FunctionDescriptor.of(JAVA_INT));

    static final MethodHandle dot8s$mh = downcallHandle("dot8s", FunctionDescriptor.of(JAVA_INT, ADDRESS, ADDRESS, JAVA_INT));
    static final MethodHandle sqr8s$mh = downcallHandle("sqr8s", FunctionDescriptor.of(JAVA_INT, ADDRESS, ADDRESS, JAVA_INT));

    // Stride of the native implementation - consumes this number of bytes per loop invocation.
    // There must be at least this number of bytes/elements available when going native
    static final int DOT_STRIDE = 32;
    static final int SQR_STRIDE = 16;

    static {
        assert DOT_STRIDE > 0 && (DOT_STRIDE & (DOT_STRIDE - 1)) == 0 : "Not a power of two";
        assert dot8Stride() == DOT_STRIDE : dot8Stride() + " != " + DOT_STRIDE;
        assert SQR_STRIDE > 0 && (SQR_STRIDE & (SQR_STRIDE - 1)) == 0 : "Not a power of two";
        assert sqr8Stride() == SQR_STRIDE : sqr8Stride() + " != " + SQR_STRIDE;
    }

    /**
     * Computes the dot product of given byte vectors.
     * @param a address of the first vector
     * @param b address of the second vector
     * @param length the vector dimensions
     */
    static int dotProduct(MemorySegment a, MemorySegment b, int length) {
        assert length >= 0;
        if (a.byteSize() != b.byteSize()) {
            throw new IllegalArgumentException("dimensions differ: " + a.byteSize() + "!=" + b.byteSize());
        }
        if (length > a.byteSize()) {
            throw new IllegalArgumentException("length: " + length + ", greater than vector dimensions: " + a.byteSize());
        }
        int i = 0;
        int res = 0;
        if (length >= DOT_STRIDE) {
            i += length & ~(DOT_STRIDE - 1);
            res = dot8s(a, b, i);
        }

        // tail
        for (; i < length; i++) {
            res += a.get(JAVA_BYTE, i) * b.get(JAVA_BYTE, i);
        }
        assert i == length;
        return res;
    }

    /**
     * Computes the square distance of given byte vectors.
     * @param a address of the first vector
     * @param b address of the second vector
     * @param length the vector dimensions
     */
    static int squareDistance(MemorySegment a, MemorySegment b, int length) {
        assert length >= 0;
        if (a.byteSize() != b.byteSize()) {
            throw new IllegalArgumentException("dimensions differ: " + a.byteSize() + "!=" + b.byteSize());
        }
        if (length > a.byteSize()) {
            throw new IllegalArgumentException("length: " + length + ", greater than vector dimensions: " + a.byteSize());
        }
        int i = 0;
        int res = 0;
        if (length >= SQR_STRIDE) {
            i += length & ~(SQR_STRIDE - 1);
            res = sqr8s(a, b, i);
        }

        // tail
        for (; i < length; i++) {
            int dist = a.get(JAVA_BYTE, i) - b.get(JAVA_BYTE, i);
            res += dist * dist;
        }
        assert i == length;
        return res;
    }

    private static int dot8Stride() {
        try {
            return (int) dot8stride$mh.invokeExact();
        } catch (Throwable t) {
            throw new AssertionError(t);
        }
    }

    private static int sqr8Stride() {
        try {
            return (int) sqr8stride$mh.invokeExact();
        } catch (Throwable t) {
            throw new AssertionError(t);
        }
    }

    private static int dot8s(MemorySegment a, MemorySegment b, int length) {
        try {
            return (int) dot8s$mh.invokeExact(a, b, length);
        } catch (Throwable t) {
            throw new AssertionError(t);
        }
    }

    private static int sqr8s(MemorySegment a, MemorySegment b, int length) {
        try {
            return (int) sqr8s$mh.invokeExact(a, b, length);
        } catch (Throwable t) {
            throw new AssertionError(t);
        }
    }

    static final MethodHandle DOT_HANDLE;
    static final MethodHandle SQR_HANDLE;

    static {
        try {
            var lookup = MethodHandles.lookup();
            var mt = MethodType.methodType(int.class, MemorySegment.class, MemorySegment.class, int.class);
            DOT_HANDLE = lookup.findStatic(JdkVectorLibrary.class, "dotProduct", mt);
            SQR_HANDLE = lookup.findStatic(JdkVectorLibrary.class, "squareDistance", mt);
        } catch (NoSuchMethodException | IllegalAccessException e) {
            throw new RuntimeException(e);
        }
    }

    @Override
    public MethodHandle dotProductHandle() {
        return DOT_HANDLE;
    }

    @Override
    public MethodHandle squareDistanceHandle() {
        return SQR_HANDLE;
    }
}

private class IpPrefixLeafCollector extends LeafBucketCollectorBase {
        private final IpPrefix ipPrefix;
        private final LeafBucketCollector sub;
        private final SortedBinaryDocValues values;

        IpPrefixLeafCollector(final LeafBucketCollector sub, final SortedBinaryDocValues values, final IpPrefix ipPrefix) {
            super(sub, values);
            this.sub = sub;
            this.values = values;
            this.ipPrefix = ipPrefix;
        }

        @Override
        public void collect(int doc, long owningBucketOrd) throws IOException {
            BytesRef previousSubnet = null;
            BytesRef subnet = new BytesRef(new byte[ipPrefix.netmask.length]);
            BytesRef ipAddress;
            if (values.advanceExact(doc)) {
                int valuesCount = values.docValueCount();

                for (int i = 0; i < valuesCount; ++i) {
                    ipAddress = values.nextValue();
                    maskIpAddress(ipAddress, ipPrefix.netmask, subnet);
                    if (previousSubnet != null && subnet.bytesEquals(previousSubnet)) {
                        continue;
                    }
                    long bucketOrd = bucketOrds.add(owningBucketOrd, subnet);
                    if (bucketOrd < 0) {
                        bucketOrd = -1 - bucketOrd;
                        collectExistingBucket(sub, doc, bucketOrd);
                    } else {
                        collectBucket(sub, doc, bucketOrd);
                    }
                    previousSubnet = subnet;
                }
            }
        }

        private static void maskIpAddress(final BytesRef ipAddress, final BytesRef subnetMask, final BytesRef subnet) {
            assert ipAddress.length == 16 : "Invalid length for ip address [" + ipAddress.length + "] expected 16 bytes";
            // NOTE: IPv4 addresses are encoded as 16-bytes. As a result, we use an
            // offset (12) to apply the subnet to the last 4 bytes (byes 12, 13, 14, 15)
            // if the subnet mask is just a 4-bytes subnet mask.
            int offset = subnetMask.length == 4 ? 12 : 0;
            for (int i = 0; i < subnetMask.length; ++i) {
                subnet.bytes[i] = (byte) (ipAddress.bytes[i + offset] & subnetMask.bytes[i]);
            }
        }
    }

/**
 * This class performs the actual work with JNA and library bindings to call native methods. It should only be used after
 * we are sure that the JNA classes are available to the JVM
 */
class JNANatives {

    /** no instantiation */
    private JNANatives() {}

    private static final Logger logger = LogManager.getLogger(JNANatives.class);

    // Set to true, in case native mlockall call was successful
    static boolean LOCAL_MLOCKALL = false;
    // Set to true, in case native system call filter install was successful
    static boolean LOCAL_SYSTEM_CALL_FILTER = false;
    // Set to true, in case policy can be applied to all threads of the process (even existing ones)
    // otherwise they are only inherited for new threads (ES app threads)
    static boolean LOCAL_SYSTEM_CALL_FILTER_ALL = false;

    static void tryMlockall() {
        int errno = Integer.MIN_VALUE;
        String errMsg = null;
        boolean rlimitSuccess = false;
        long softLimit = 0;
        long hardLimit = 0;

        try {
            int result = JNACLibrary.mlockall(JNACLibrary.MCL_CURRENT);
            if (result == 0) {
                LOCAL_MLOCKALL = true;
                return;
            }

            errno = Native.getLastError();
            errMsg = JNACLibrary.strerror(errno);
            if (Constants.LINUX || Constants.MAC_OS_X) {
                // we only know RLIMIT_MEMLOCK for these two at the moment.
                JNACLibrary.Rlimit rlimit = new JNACLibrary.Rlimit();
                if (JNACLibrary.getrlimit(JNACLibrary.RLIMIT_MEMLOCK, rlimit) == 0) {
                    rlimitSuccess = true;
                    softLimit = rlimit.rlim_cur.longValue();
                    hardLimit = rlimit.rlim_max.longValue();
                } else {
                    logger.warn("Unable to retrieve resource limits: {}", JNACLibrary.strerror(Native.getLastError()));
                }
            }
        } catch (UnsatisfiedLinkError e) {
            // this will have already been logged by CLibrary, no need to repeat it
            return;
        }

        // mlockall failed for some reason
        logger.warn("Unable to lock JVM Memory: error={}, reason={}", errno, errMsg);
        logger.warn("This can result in part of the JVM being swapped out.");
        if (errno == JNACLibrary.ENOMEM) {
            if (rlimitSuccess) {
                logger.warn(
                    "Increase RLIMIT_MEMLOCK, soft limit: {}, hard limit: {}",
                    rlimitToString(softLimit),
                    rlimitToString(hardLimit)
                );
                if (Constants.LINUX) {
                    // give specific instructions for the linux case to make it easy
                    String user = System.getProperty("user.name");
                    logger.warn("""
                        These can be adjusted by modifying /etc/security/limits.conf, for example:
                        \t# allow user '{}' mlockall
                        \t{} soft memlock unlimited
                        \t{} hard memlock unlimited""", user, user, user);
                    logger.warn("If you are logged in interactively, you will have to re-login for the new limits to take effect.");
                }
            } else {
                logger.warn("Increase RLIMIT_MEMLOCK (ulimit).");
            }
        }
    }

    static String rlimitToString(long value) {
        assert Constants.LINUX || Constants.MAC_OS_X;
        if (value == JNACLibrary.RLIM_INFINITY) {
            return "unlimited";
        } else {
            return Long.toUnsignedString(value);
        }
    }

    static void tryVirtualLock() {
        JNAKernel32Library kernel = JNAKernel32Library.getInstance();
        Pointer process = null;
        try {
            process = kernel.GetCurrentProcess();
            // By default, Windows limits the number of pages that can be locked.
            // Thus, we need to first increase the working set size of the JVM by
            // the amount of memory we wish to lock, plus a small overhead (1MB).
            SizeT size = new SizeT(JvmInfo.jvmInfo().getMem().getHeapInit().getBytes() + (1024 * 1024));
            if (kernel.SetProcessWorkingSetSize(process, size, size) == false) {
                logger.warn("Unable to lock JVM memory. Failed to set working set size. Error code {}", Native.getLastError());
            } else {
                JNAKernel32Library.MemoryBasicInformation memInfo = new JNAKernel32Library.MemoryBasicInformation();
                long address = 0;
                while (kernel.VirtualQueryEx(process, new Pointer(address), memInfo, memInfo.size()) != 0) {
                    boolean lockable = memInfo.State.longValue() == JNAKernel32Library.MEM_COMMIT
                        && (memInfo.Protect.longValue() & JNAKernel32Library.PAGE_NOACCESS) != JNAKernel32Library.PAGE_NOACCESS
                        && (memInfo.Protect.longValue() & JNAKernel32Library.PAGE_GUARD) != JNAKernel32Library.PAGE_GUARD;
                    if (lockable) {
                        kernel.VirtualLock(memInfo.BaseAddress, new SizeT(memInfo.RegionSize.longValue()));
                    }
                    // Move to the next region
                    address += memInfo.RegionSize.longValue();
                }
                LOCAL_MLOCKALL = true;
            }
        } catch (UnsatisfiedLinkError e) {
            // this will have already been logged by Kernel32Library, no need to repeat it
        } finally {
            if (process != null) {
                kernel.CloseHandle(process);
            }
        }
    }

    /**
     * Retrieves the short path form of the specified path.
     *
     * @param path the path
     * @return the short path name (or the original path if getting the short path name fails for any reason)
     */
    static String getShortPathName(String path) {
        assert Constants.WINDOWS;
        try {
            final WString longPath = new WString("\\\\?\\" + path);
            // first we get the length of the buffer needed
            final int length = JNAKernel32Library.getInstance().GetShortPathNameW(longPath, null, 0);
            if (length == 0) {
                logger.warn("failed to get short path name: {}", Native.getLastError());
                return path;
            }
            final char[] shortPath = new char[length];
            // knowing the length of the buffer, now we get the short name
            if (JNAKernel32Library.getInstance().GetShortPathNameW(longPath, shortPath, length) > 0) {
                return Native.toString(shortPath);
            } else {
                logger.warn("failed to get short path name: {}", Native.getLastError());
                return path;
            }
        } catch (final UnsatisfiedLinkError e) {
            return path;
        }
    }

    static void addConsoleCtrlHandler(ConsoleCtrlHandler handler) {
        // The console Ctrl handler is necessary on Windows platforms only.
        if (Constants.WINDOWS) {
            try {
                boolean result = JNAKernel32Library.getInstance().addConsoleCtrlHandler(handler);
                if (result) {
                    logger.debug("console ctrl handler correctly set");
                } else {
                    logger.warn("unknown error {} when adding console ctrl handler", Native.getLastError());
                }
            } catch (UnsatisfiedLinkError e) {
                // this will have already been logged by Kernel32Library, no need to repeat it
            }
        }
    }

    static void tryInstallSystemCallFilter(Path tmpFile) {
        try {
            int ret = SystemCallFilter.init(tmpFile);
            LOCAL_SYSTEM_CALL_FILTER = true;
            if (ret == 1) {
                LOCAL_SYSTEM_CALL_FILTER_ALL = true;
            }
        } catch (Exception e) {
            // this is likely to happen unless the kernel is newish, its a best effort at the moment
            // so we log stacktrace at debug for now...
            if (logger.isDebugEnabled()) {
                logger.debug("unable to install syscall filter", e);
            }
            logger.warn("unable to install syscall filter: ", e);
        }
    }

}

public final class SourceUtils {

    private SourceUtils() {}

    public static void writeSource(StreamOutput out, Source source) throws IOException {
        writeSource(out, source, true);
    }

    public static void writeSourceNoText(StreamOutput out, Source source) throws IOException {
        writeSource(out, source, false);
    }

    public static Source readSource(StreamInput in) throws IOException {
        return readSource(in, null);
    }

    public static Source readSourceWithText(StreamInput in, String queryText) throws IOException {
        return readSource(in, queryText);
    }

    private static void writeSource(StreamOutput out, Source source, boolean writeText) throws IOException {
        out.writeInt(source.source().getLineNumber());
        out.writeInt(source.source().getColumnNumber());
        if (writeText) {
            out.writeString(source.text());
        } else {
            out.writeInt(source.text().length());
        }
    }

    private static Source readSource(StreamInput in, @Nullable String queryText) throws IOException {
        int line = in.readInt();
        int column = in.readInt();
        int charPositionInLine = column - 1;

        String text;
        if (queryText == null) {
            text = in.readString();
        } else {
            int length = in.readInt();
            text = sourceText(queryText, line, column, length);
        }
        return new Source(new Location(line, charPositionInLine), text);
    }

    private static String sourceText(String query, int line, int column, int length) {
        if (line <= 0 || column <= 0 || query.isEmpty()) {
            return StringUtils.EMPTY;
        }
        int offset = textOffset(query, line, column);
        if (offset + length > query.length()) {
            throw new QlIllegalArgumentException(
                "location [@" + line + ":" + column + "] and length [" + length + "] overrun query size [" + query.length() + "]"
            );
        }
        return query.substring(offset, offset + length);
    }

    private static int textOffset(String query, int line, int column) {
        int offset = 0;
        if (line > 1) {
            String[] lines = query.split("\n");
            if (line > lines.length) {
                throw new QlIllegalArgumentException(
                    "line location [" + line + "] higher than max [" + lines.length + "] in query [" + query + "]"
                );
            }
            for (int i = 0; i < line - 1; i++) {
                offset += lines[i].length() + 1; // +1 accounts for the removed \n
            }
        }
        offset += column - 1; // -1 since column is 1-based indexed
        return offset;
    }
}

