        List<PlanNameRegistry.Entry> entries = new ArrayList<>(declared);

        // From NamedWriteables
        for (NamedWriteableRegistry.Entry e : UnaryScalarFunction.getNamedWriteables()) {
            entries.add(of(ESQL_UNARY_SCLR_CLS, e));
