        // allowNoIndices
        in.readOptionalString();
        // ignoreUnavailable
        in.readOptionalString();
        // preference
        in.readOptionalString();
    }

    private static void writeEsSourceOptions(PlanStreamOutput out) throws IOException {
        // allowNoIndices
        out.writeOptionalString(null);
        // ignoreUnavailable
        out.writeOptionalString(null);
