          JOptionPane.showMessageDialog(ConfigFrame.this,
                                        "This is not a valid class name:\n"+
                                        s,
                                        "Error Adding Class Name",
                                        JOptionPane.ERROR_MESSAGE); 
        }
        return result;
      }
    };
