    public int getParentCategoryID() { return parentCategoryID; }

    public AttachmentXML() {
        super();
        attachmentID=-1;
        parentPostID=-1;
        parentThreadID=-1;
        parentForumID=-1;
        parentCategoryID=-1;
    }

