public class BinaryStorageHandle {

    private int type;
    private int primaryKey;
    private String fileName;

    public BinaryStorageHandle(int type, int primaryKey, String fileName) {
        this.type = type;
        this.primaryKey = primaryKey;
        this.fileName = fileName ;
    }

    public String getFileName() {
        return fileName;
    }
    public void setFileName(String fileName) {
        this.fileName = fileName;
    }

    public int getPrimaryKey() {
        return primaryKey;
    }
    public void setPrimaryKey(int primaryKey) {
        this.primaryKey = primaryKey;
    }

    public int getType() {
        return type;
    }
    public void setType(int type) {
        this.type = type;
    }

	public byte[] loadData(String category, String nameId, BinaryStorageServiceImplBase binaryStorageServiceImplBase)
	    throws IOException {

	    InputStream inputStream = binaryStorageServiceImplBase.getInputStream(category, nameId, this);
	    return FileUtil.getBytes(inputStream);
	}
}
