public final class InterceptorService {

    private static final Log log = LogFactory.getLog(InterceptorService.class);

    //private static final String OPTION_FILE_NAME = "mvncore.xml";

    /** Singleton instance of this class */
    private static InterceptorService instance      = new InterceptorService();

    private MailInterceptor mailInterceptor         = null;

    private ContentInterceptor contentInterceptor   = null;

    private LoginIDInterceptor loginIDInterceptor   = null;

    private PasswordInterceptor passwordInterceptor = null;
    /**
     * Private constructor to prevent instantiation
     */
    private InterceptorService() {
        loadInterceptorInfo();
    }

    private void loadInterceptorInfo() {
        String mailInterceptorClassName = MVNCoreConfig.getMailInterceptorClassName();
        loadMailInterceptor(mailInterceptorClassName);

        String contentInterceptorClassName = MVNCoreConfig.getContentInterceptorClassName();
        loadContentInterceptor(contentInterceptorClassName);

        String loginIDInterceptorClassName = MVNCoreConfig.getLoginIdInterceptorClassName();
        loadLoginIDInterceptor(loginIDInterceptorClassName);

        String passwordInterceptorClassName = MVNCoreConfig.getPasswordInterceptorClassName();
        loadPasswordInterceptor(passwordInterceptorClassName);

    }

    private void loadContentInterceptor(String contentInterceptorClassName) {

        try {
            if (contentInterceptorClassName.length() > 0) {
                Class contentInterceptorClass = Class.forName(contentInterceptorClassName);
                ContentInterceptor interceptor = (ContentInterceptor)contentInterceptorClass.newInstance();
                setContentInterceptor(interceptor);
            }
        } catch (Exception ex) {
            log.error("Cannot load ContentInterceptor", ex);
        }
    }

    private void loadMailInterceptor(String mailInterceptorClassName) {

        try {
            if (mailInterceptorClassName.length() > 0) {
                Class mailInterceptorClass = Class.forName(mailInterceptorClassName);
                MailInterceptor interceptor = (MailInterceptor)mailInterceptorClass.newInstance();
                setMailInterceptor(interceptor);
            }
        } catch (Exception ex) {
            log.error("Cannot load MailInterceptor", ex);
        }
    }

    private void loadLoginIDInterceptor(String loginIDInterceptorClassName) {

        try {
            if (loginIDInterceptorClassName.length() > 0) {
                Class loginIDInterceptorClass = Class.forName(loginIDInterceptorClassName);
                LoginIDInterceptor interceptor = (LoginIDInterceptor)loginIDInterceptorClass.newInstance();
                setLoginIDInterceptor(interceptor);
            }
        } catch (Exception ex) {
            log.error("Cannot load LoginIDInterceptor", ex);
        }
    }

    private void loadPasswordInterceptor(String passwordInterceptorClassName) {

        try {
            if (passwordInterceptorClassName.length() > 0) {
                Class passwordInterceptorClass = Class.forName(passwordInterceptorClassName);

                PasswordInterceptor interceptor = (PasswordInterceptor)passwordInterceptorClass.newInstance();
                setPasswordInterceptor(interceptor);
            }
        } catch (Exception ex) {
            log.error("Cannot load PasswordInterceptor", ex);
        }
    }

    /**
     * Return singleton instance of this class
     *
     * @return InterceptorService the singleton instance of this class
     */
    public static InterceptorService getInstance() {
        return instance;
    }

    /**
     * Validate email if the MailInterceptor is present
     *
     * @param email String email to be validated
     * @throws InterceptorException if email is not valid for some reason
     */
    public void validateMail(String email) throws InterceptorException {
        if (mailInterceptor != null) {
            mailInterceptor.validateEmail(email);
        }
    }

    public MailInterceptor getMailInterceptor() {
        return mailInterceptor;
    }

    public void setMailInterceptor(MailInterceptor interceptor) {
        log.info("Use MailInterceptor = " + interceptor);
        this.mailInterceptor = interceptor;
    }

    /**
     * Validate content if the ContentInterceptor is present
     *
     * @param content String content to be validated
     * @return the new content
     * @throws InterceptorException if content is not valid for some reason
     */
    public String validateContent(String content) throws InterceptorException {
        if (contentInterceptor != null) {
            return contentInterceptor.validateContent(content);
        }
        return content;
    }

    public ContentInterceptor getContentInterceptor() {
        return contentInterceptor;
    }

    public void setContentInterceptor(ContentInterceptor interceptor) {
        log.info("Use ContentInterceptor = " + interceptor);
        this.contentInterceptor = interceptor;
    }

    /**
     * Validate loginID if the LoginIDInterceptor is present
     *
     * @param loginID String loginID to be validated
     * @throws InterceptorException if loginID is not valid for some reason
     */
    public void validateLoginID(String loginID) throws InterceptorException {
        if (loginIDInterceptor != null) {
            loginIDInterceptor.validateLoginID(loginID);
        }
    }

    public LoginIDInterceptor getLoginIDInterceptor() {
        return loginIDInterceptor;
    }

    public void setLoginIDInterceptor(LoginIDInterceptor interceptor) {
        log.info("Use LoginIDInterceptor = " + interceptor);
        this.loginIDInterceptor = interceptor;
    }

    /**
     * Validate password if the PasswordInterceptor is present
     *
     * @param password String password to be validated
     * @throws InterceptorException if password is not valid for some reason
     */
    public void validatePassword(String password) throws InterceptorException {
        if (passwordInterceptor != null) {
            passwordInterceptor.validatePassword(password);
        }
    }

    public PasswordInterceptor getPasswordInterceptor() {
        return passwordInterceptor;
    }

    public void setPasswordInterceptor(PasswordInterceptor interceptor) {
        log.info("Use PasswordInterceptor = " + interceptor);
        this.passwordInterceptor = interceptor;
    }

	/**
	 * This method is for addpost page
	 * @param postWebHandler TODO
	 * @param request TODO
	 * @param response TODO
	 * @throws InterceptorException
	 */
	public void prepareAdd(PostWebHandler postWebHandler, GenericRequest request, GenericResponse response)
	    throws ObjectNotFoundException, DatabaseException, BadInputException, AuthenticationException, InterceptorException {

	    Locale locale = I18nUtil.getLocaleInRequest(request);

	    if (MVNForumConfig.getEnableNewPost() == false) {
	        String localizedMessage = MVNForumResourceBundle.getString(locale, "java.lang.IllegalStateException.cannot_create_new_post.new_post_is_disabled");
	        throw new IllegalStateException(localizedMessage);
	        //throw new IllegalStateException("Cannot create new post because NEW_POST feature is disabled by administrator.");
	    }

	    OnlineUser onlineUser = postWebHandler.onlineUserManager.getOnlineUser(request);
	    MVNForumPermission permission = onlineUser.getPermission();

	    if (MVNForumConfig.isGuestUserInDatabase() == false) {
	        permission.ensureIsAuthenticated();
	    }

	    // we set this action attribute first because the return below can make method return prematurely
	    request.setAttribute("action", "addnew");

	    int parentPostID    = 0;
	    try {
	        // if has parent --> there is no forum
	        parentPostID = GenericParamUtil.getParameterInt(request, "parent");
	    } catch (Exception ex) {
	        // do nothing
	        // NOTE: we cannot return here since user can have a parameter parent = 0
	    }
	    int forumID;
	    String mode;
	    String replyTopic;

	    if (parentPostID == 0) {// new thread
	        mode = MVNForumResourceBundle.getString(locale, "mvnforum.user.addpost.mode.addnew");
	        replyTopic = "";
	        forumID = GenericParamUtil.getParameterInt(request, "forum");

	        ForumBean forumBean = null;
	        try {
	            forumBean = ForumCache.getInstance().getBean(forumID);
	        } catch (ObjectNotFoundException e) {
	            String localizedMessage = MVNForumResourceBundle.getString(locale, "mvncore.exception.ObjectNotFoundException.forumid_not_exists", new Object[] {new Integer(forumID)});
	            throw new ObjectNotFoundException(localizedMessage);
	        }
	        forumBean.ensureNotDisabledForum(locale);
	        forumBean.ensureNotClosedForum(locale);
	        forumBean.ensureNotLockedForum(locale);

	        permission.ensureCanAddThread(forumID);

	        // check if this thread is being watched
	        Boolean isWatched = Boolean.FALSE;
	        if (onlineUser.isMember()) {
	            isWatched = Boolean.valueOf(WatchUtil.isForumWatched(onlineUser.getMemberID(), forumBean));
	        }
	        request.setAttribute("isWatched", isWatched);
	    } else {// reply to a post
	        // this is a parent post
	        mode = MVNForumResourceBundle.getString(locale, "mvnforum.user.addpost.mode.reply");
	        PostBean postBean = null;
	        try {
	            postBean = DAOFactory.getPostDAO().getPost(parentPostID);// can throw DatabaseException
	        } catch (ObjectNotFoundException ex) {
	            String localizedMessage = MVNForumResourceBundle.getString(locale, "mvncore.exception.ObjectNotFoundException.postid_not_exists", new Object[] {new Integer(parentPostID)});
	            throw new ObjectNotFoundException(localizedMessage);
	        }

	        // check permission
	        forumID  = postBean.getForumID();
	        replyTopic = postBean.getPostTopic();
	        ForumBean forumBean = null;
	        try {
	            forumBean = ForumCache.getInstance().getBean(forumID);
	        } catch (ObjectNotFoundException e) {
	            String localizedMessage = MVNForumResourceBundle.getString(locale, "mvncore.exception.ObjectNotFoundException.forumid_not_exists", new Object[] {new Integer(forumID)});
	            throw new ObjectNotFoundException(localizedMessage);
	        }
	        forumBean.ensureNotDisabledForum(locale);
	        forumBean.ensureNotClosedForum(locale);
	        forumBean.ensureNotLockedForum(locale);

	        permission.ensureCanAddPost(forumID);

	        // now we prepare to list latest post in the thread
	        int threadID = postBean.getThreadID();

	        // now check if thread is closed or locked, if it is, then cannot reply to a post
	        ThreadBean threadBean = null;
	        try {
	            threadBean = DAOFactory.getThreadDAO().getThread(threadID);
	        } catch (ObjectNotFoundException ex) {
	            String localizedMessage = MVNForumResourceBundle.getString(locale, "mvncore.exception.ObjectNotFoundException.threadid_not_exists", new Object[] {new Integer(threadID)});
	            throw new ObjectNotFoundException(localizedMessage);
	        }

	        threadBean.ensureStatusCanReply();

	        Collection postBeans = DAOFactory.getPostDAO().getLastEnablePosts_inThread_limit(threadID, MVNForumConfig.ROWS_IN_LAST_REPLIES);
	        request.setAttribute("ParentPostBean", postBean);
	        request.setAttribute("PostBeans", postBeans);

	        // check if this thread is being watched
	        Boolean isWatched = Boolean.FALSE;
	        if (onlineUser.isMember()) {
	            isWatched = Boolean.valueOf(WatchUtil.isThreadWatched(onlineUser.getMemberID(), threadBean, forumBean));
	        }
	        request.setAttribute("isWatched", isWatched);
	    }

	    boolean isPreviewing = GenericParamUtil.getParameterBoolean(request, "preview");
	    if (isPreviewing) {
	        MyUtil.saveVNTyperMode(request, response);
	        //Get and Check if user enter some text or not
	        String prePostTopic = GenericParamUtil.getParameter(request, "PostTopic", true);
	        String prePostBody  = GenericParamUtil.getParameter(request, "message", true);
	        String prePostIcon  = GenericParamUtil.getParameter(request, "PostIcon", false);

	        //always disable HTML
	        prePostTopic = DisableHtmlTagFilter.filter(prePostTopic);
	        prePostBody  = DisableHtmlTagFilter.filter(prePostBody);
	        prePostIcon  = DisableHtmlTagFilter.filter(prePostIcon);

	        prePostTopic = validateContent(prePostTopic);
	        prePostBody  = validateContent(prePostBody);

	        request.setAttribute("prePostTopic", prePostTopic);
	        request.setAttribute("prePostBody", prePostBody);
	        request.setAttribute("prePostIcon", prePostIcon);

	        MemberBean memberBean = MemberCache.getInstance().getMember(onlineUser.getMemberID());
	        request.setAttribute("MemberBean", memberBean);
	    }

	    CategoryBuilder treebuilder = PostWebHandler.categoryBuilderService.getCategoryTreeBuilder();
	    CategoryTree categorytree = new CategoryTree(treebuilder);
	    CategoryTreeListener treelistener = PostWebHandler.categoryService.getCategoryTreePath(request, response, forumID, mode, "addnew", replyTopic);
	    categorytree.addCategeoryTreeListener(treelistener);
	    request.setAttribute("tree", categorytree.build());

	    CategoryBuilder builder = PostWebHandler.categoryBuilderService.getCategoryTreeBuilder();
	    CategoryTree tree = new CategoryTree(builder);
	    CategoryTreeListener listener = PostWebHandler.categoryService.getManagementCategorySelector(request, response, "addpost");
	    tree.addCategeoryTreeListener(listener);
	    request.setAttribute("Result", tree.build());
	}

}

public class ForumIconLegend {

    public static final String FORUM_ICON_READ_ACTIVE       = "f_read_active.gif";
    public static final String FORUM_ICON_READ_CLOSED       = "f_read_closed.gif";
    public static final String FORUM_ICON_READ_LOCKED       = "f_read_locked.gif";
    public static final String FORUM_ICON_READ_DISABLED     = "f_read_disabled.gif";
    public static final String FORUM_ICON_UNREAD_ACTIVE     = "f_unread_active.gif";
    public static final String FORUM_ICON_UNREAD_CLOSED     = "f_unread_closed.gif";
    public static final String FORUM_ICON_UNREAD_LOCKED     = "f_unread_locked.gif";
    public static final String FORUM_ICON_UNREAD_DISABLED   = "f_unread_disabled.gif";

    private boolean hasReadActiveForum        = false;
    private boolean hasUnreadActiveForum      = false;
    private boolean hasReadClosedForum        = false;
    private boolean hasUnreadClosedForum      = false;
    private boolean hasReadLockedForum        = false;
    private boolean hasUnreadLockedForum      = false;
    private boolean hasReadDisabledForum      = false;
    private boolean hasUnreadDisabledForum    = false;

    public ForumIconLegend() {

    }

    public void updateIconLegend(String forumIcon) {
        if (forumIcon.equals(FORUM_ICON_READ_ACTIVE)) {
            hasReadActiveForum = true;
        } else if (forumIcon.equals(FORUM_ICON_READ_CLOSED)) {
            hasReadClosedForum = true;
        } else if (forumIcon.equals(FORUM_ICON_READ_LOCKED)) {
            hasReadLockedForum = true;
        } else if (forumIcon.equals(FORUM_ICON_READ_DISABLED)) {
            hasReadDisabledForum = true;
        } else if (forumIcon.equals(FORUM_ICON_UNREAD_ACTIVE)) {
            hasUnreadActiveForum = true;
        } else if (forumIcon.equals(FORUM_ICON_UNREAD_CLOSED)) {
            hasUnreadClosedForum = true;
        } else if (forumIcon.equals(FORUM_ICON_UNREAD_LOCKED)) {
            hasUnreadLockedForum = true;
        } else if (forumIcon.equals(FORUM_ICON_UNREAD_DISABLED)) {
            hasUnreadDisabledForum = true;
        }
    }

    public boolean isHasReadActiveForum() {
        return hasReadActiveForum;
    }

    public boolean isHasReadClosedForum() {
        return hasReadClosedForum;
    }

    public boolean isHasReadLockedForum() {
        return hasReadLockedForum;
    }

    public boolean isHasReadDisabledForum() {
        return hasReadDisabledForum;
    }

    public boolean isHasUnreadActiveForum() {
        return hasUnreadActiveForum;
    }

    public boolean isHasUnreadClosedForum() {
        return hasUnreadClosedForum;
    }

    public boolean isHasUnreadLockedForum() {
        return hasUnreadLockedForum;
    }

    public boolean isHasUnreadDisabledForum() {
        return hasUnreadDisabledForum;
    }

    public boolean isHasIconLegend() {
        return (hasReadActiveForum || hasUnreadActiveForum || hasReadClosedForum || 
                hasUnreadClosedForum || hasReadLockedForum || hasUnreadLockedForum || 
                hasReadDisabledForum || hasUnreadDisabledForum);
    }

	public String drawForum2(CategoryTreeListenerImpl categoryTreeListenerImpl, CategoryTreeEvent event) {

	    SimpleHash subRoot = new SimpleHash();
	    subRoot.put("RowType", "Forum");

	    ForumBean forum = (ForumBean) event.getSource();

	    boolean canReadPost = categoryTreeListenerImpl.permission.canReadPost(forum.getForumID());
	    subRoot.put("CanReadPost", canReadPost);
	    if (canReadPost) {
	        boolean canEditForum = categoryTreeListenerImpl.permission.canEditForum(forum.getForumID());
	        subRoot.put("CanEditForum", canEditForum);
	        if (canEditForum) {
	            String forumUpLink = CategoryTreeListenerImpl.urlResolver.encodeURL(categoryTreeListenerImpl.request, categoryTreeListenerImpl.response, "updateforumorder?forum=" + forum.getForumID() + "&amp;action=up", URLResolverService.ACTION_URL);
	            subRoot.put("ForumUpLink", forumUpLink);
	            subRoot.put("moveUp", MVNForumResourceBundle.getString(categoryTreeListenerImpl.locale, "mvnforum.common.order.move_up"));

	            String forumDownLink = CategoryTreeListenerImpl.urlResolver.encodeURL(categoryTreeListenerImpl.request, categoryTreeListenerImpl.response, "updateforumorder?forum=" + forum.getForumID() + "&amp;action=down", URLResolverService.ACTION_URL);
	            subRoot.put("ForumDownLink", forumDownLink);
	            subRoot.put("moveDown", MVNForumResourceBundle.getString(categoryTreeListenerImpl.locale, "mvnforum.common.order.move_down"));

	            String editForumLink = CategoryTreeListenerImpl.urlResolver.encodeURL(categoryTreeListenerImpl.request, categoryTreeListenerImpl.response, "editforum?forum=" + forum.getForumID());
	            subRoot.put("EditForumLink", editForumLink);
	        }

	        boolean canDeleteForum = categoryTreeListenerImpl.permission.canDeleteForum(forum.getForumID());
	        subRoot.put("CanDeleteForum", canDeleteForum);
	        if (canDeleteForum) {
	            String deleteForumLink = CategoryTreeListenerImpl.urlResolver.encodeURL(categoryTreeListenerImpl.request, categoryTreeListenerImpl.response, "deleteforum?forum=" + forum.getForumID());
	            subRoot.put("DeleteForumLink", deleteForumLink);

	            String delete = MVNForumResourceBundle.getString(categoryTreeListenerImpl.locale, "mvnforum.common.action.delete");
	            subRoot.put("delete", delete);
	        }

	        boolean canAssignForum = categoryTreeListenerImpl.permission.canAssignToForum(forum.getForumID());
	        subRoot.put("CanAssignForum", canAssignForum);
	        if (canAssignForum) {
	            String groupAssignLink = CategoryTreeListenerImpl.urlResolver.encodeURL(categoryTreeListenerImpl.request, categoryTreeListenerImpl.response, "assigngrouptoforum?forum=" + forum.getForumID());
	            subRoot.put("GroupAssignLink", groupAssignLink);

	            String memberAssignLink = CategoryTreeListenerImpl.urlResolver.encodeURL(categoryTreeListenerImpl.request, categoryTreeListenerImpl.response, "assignmembertoforum?forum=" + forum.getForumID());
	            subRoot.put("MemberAssignLink", memberAssignLink);
	        }

	        subRoot.put("ContextPath", categoryTreeListenerImpl.request.getContextPath());
	        subRoot.put("Disabled", (forum.getForumStatus() == ForumBean.FORUM_STATUS_DISABLED));
	        subRoot.put("ForumName", forum.getForumName());

	        String forumIcon = MyUtil.getForumIconName(categoryTreeListenerImpl.onlineUser.getLastLogonTimestamp().getTime(), forum.getForumLastPostDate().getTime(), forum.getForumStatus(), forum.getForumThreadCount());
	        subRoot.put("forumIcon", forumIcon);

	        updateIconLegend(forumIcon);

	        String filteredForumDesc = MyUtil.filter(forum.getForumDesc(), false/*html*/, true/*emotion*/, true/*mvnCode*/, true/*newLine*/, true/*URL*/);
	        subRoot.put("FilteredForumDesc", filteredForumDesc);
	        subRoot.put("ForumOrder", new Integer(forum.getForumOrder()));

	        String gmtForumCreationDate = categoryTreeListenerImpl.onlineUser.getGMTTimestampFormat(forum.getForumCreationDate());
	        subRoot.put("GMTForumCreationDate", gmtForumCreationDate);
	        subRoot.put("ForumOwnerName", StringUtil.getEmptyStringIfNull(forum.getForumOwnerName()));
	        subRoot.put("ForumTypeName", LocaleMessageUtil.getForumTypeDescFromInt(categoryTreeListenerImpl.onlineUser.getLocale(), forum.getForumType()));
	        subRoot.put("ForumModeName", LocaleMessageUtil.getForumModeDescFromInt(categoryTreeListenerImpl.onlineUser.getLocale(), forum.getForumModerationMode()));
	    }
	    categoryTreeListenerImpl.rows.add(subRoot);

	    return "";
	}

}

/*
 * Included columns: WatchID, MemberID, CategoryID, ForumID, ThreadID,
 *                   WatchType, WatchOption, WatchStatus, WatchCreationDate, WatchLastSentDate,
 *                   WatchEndDate
 * Excluded columns:
 */
public class WatchBean {
    /*************************************************************************
     * NOTE: below constants MUST NOT be changed IN ALL CASES,
     *       or it will break the compatibility
     *************************************************************************/
    public final static int WATCH_OPTION_DEFAULT    = 0;
    public final static int WATCH_OPTION_LIVE       = 1;
    public final static int WATCH_OPTION_HOURLY     = 2;
    public final static int WATCH_OPTION_DAILY      = 3;
    public final static int WATCH_OPTION_WEEKLY     = 4;

    public final static int WATCH_TYPE_DEFAULT      = 0;
    public final static int WATCH_TYPE_DIGEST       = 1;
    public final static int WATCH_TYPE_NONDIGEST    = 2;

    public final static int SELECT_GLOBAL_WATCH     = 0;
    public final static int SELECT_CATEGORY_WATCH   = 1;
    public final static int SELECT_FORUM_WATCH      = 2;
    public final static int SELECT_THREAD_WATCH     = 3;

    private int watchID;
    private int memberID;
    private int categoryID;
    private int forumID;
    private int threadID;
    private int watchType;
    private int watchOption;
    private int watchStatus;
    private Timestamp watchCreationDate;
    private Timestamp watchLastSentDate;
    private Timestamp watchEndDate;

    public int getWatchID() {
        return watchID;
    }
    public void setWatchID(int watchID) {
        this.watchID = watchID;
    }

    public int getMemberID() {
        return memberID;
    }
    public void setMemberID(int memberID) {
        this.memberID = memberID;
    }

    public int getCategoryID() {
        return categoryID;
    }
    public void setCategoryID(int categoryID) {
        this.categoryID = categoryID;
    }

    public int getForumID() {
        return forumID;
    }
    public void setForumID(int forumID) {
        this.forumID = forumID;
    }

    public int getThreadID() {
        return threadID;
    }
    public void setThreadID(int threadID) {
        this.threadID = threadID;
    }

    public int getWatchType() {
        return watchType;
    }
    public void setWatchType(int watchType) {
        this.watchType = watchType;
    }

    public int getWatchOption() {
        return watchOption;
    }
    public void setWatchOption(int watchOption) {
        this.watchOption = watchOption;
    }

    public int getWatchStatus() {
        return watchStatus;
    }
    public void setWatchStatus(int watchStatus) {
        this.watchStatus = watchStatus;
    }

    public Timestamp getWatchCreationDate() {
        return watchCreationDate;
    }
    public void setWatchCreationDate(Timestamp watchCreationDate) {
        this.watchCreationDate = watchCreationDate;
    }

    public Timestamp getWatchLastSentDate() {
        return watchLastSentDate;
    }
    public void setWatchLastSentDate(Timestamp watchLastSentDate) {
        this.watchLastSentDate = watchLastSentDate;
    }

    public Timestamp getWatchEndDate() {
        return watchEndDate;
    }
    public void setWatchEndDate(Timestamp watchEndDate) {
        this.watchEndDate = watchEndDate;
    }

    // Store the ThreadBean if this is a Thread Watch
    ThreadBean threadBean = null;

    public ThreadBean getThreadBean() {
        return threadBean;
    }
    public void setThreadBean(ThreadBean threadBean) {
        this.threadBean = threadBean;
    }

    public void appendWatch2(WatchMail watchMail) throws ObjectNotFoundException, DatabaseException {

	    AssertionUtil.doAssert(watchMail.m_receiver.getMemberID() == getMemberID(), "Assertion: receiver.getMemberID() must be equal to watchBean.getMemberID()!");

	    Timestamp lastSent = getWatchLastSentDate();

	    int categoryID = getCategoryID();
	    int forumID = getForumID();
	    int threadID = getThreadID();

	    // log.debug("appendWatch called!!! c = " + categoryID + " f = " + forumID + " t = " + threadID);

	    Collection threadBeans = null;
	    if (categoryID != 0) {
	        threadBeans = DAOFactory.getThreadDAO().getEnableThreads_inCategory(categoryID, lastSent);
	    } else if (forumID != 0) {
	        threadBeans = DAOFactory.getThreadDAO().getEnableThreads_inForum(forumID, lastSent);
	    } else if (threadID != 0) {
	        if (watchMail.shouldProcessThread(threadID)) {
	            threadBeans = DAOFactory.getThreadDAO().getEnableThreads_inThread(threadID, lastSent);
	        } else {
	            return; // ignore the reduntdant thread
	        }
	    } else {
	        threadBeans = DAOFactory.getThreadDAO().getEnableThreads_inGlobal(lastSent);
	    }

	    // check his owner post
	    if (MVNForumConfig.getEnableSendWatchMailOfMyOwnPost() == false) {
	        watchMail.checkThreadOwner(threadBeans);
	    }

	    if (threadBeans.size() == 0) {
	        return; // no new thread
	    }

	    // remember that this WatchMail has process these thread
	    watchMail.rememberThread(threadBeans);

	    if (threadBeans.size() > 0) {
	        watchMail.m_receiver.createWatchMessageBean(watchMail, threadBeans);
	    }
	}
	static public void validateWatchType(int type) throws IllegalArgumentException {
        if ((type < WATCH_TYPE_DEFAULT) || (type > WATCH_TYPE_NONDIGEST)) {
            throw new IllegalArgumentException("Invalid WatchType = " + type);
        }
    }

    static public void validateWatchOption(int option) throws IllegalArgumentException {
        if ((option < WATCH_OPTION_DEFAULT) || (option > WATCH_OPTION_WEEKLY)) {
            throw new IllegalArgumentException("Invalid WatchOption = " + option);
        }
    }

    static public void validateWatchSelect(int select) throws IllegalArgumentException {
        if ((select < SELECT_GLOBAL_WATCH) || (select > SELECT_THREAD_WATCH)) {
            throw new IllegalArgumentException("Invalid WatchSelect = " + select);
        }
    }

} //end of class WatchBean

/*
 * Included columns: PostID, ParentPostID, ForumID, ThreadID, MemberID,
 *                   MemberName, LastEditMemberName, PostTopic, PostBody, PostCreationDate,
 *                   PostLastEditDate, PostCreationIP, PostLastEditIP, PostEditCount, PostFormatOption,
 *                   PostOption, PostStatus, PostIcon, PostAttachCount
 * Excluded columns:
 */
public class PostBean {
    /*************************************************************************
     * NOTE: below constants MUST NOT be changed IN ALL CASES,
     *       or it will break the compatibility
     *************************************************************************/
    /**
     * The default value mean post is enable and normal
     */
