	public String drawForum2(ListForums listForums, CategoryTreeEvent event) {

	    ForumBean forum = (ForumBean) event.getSource();
	    SimpleHash row = new SimpleHash();

	    boolean checkForumToSee = ((forum.getCategoryID() == getCategoryID()) && listForums.permission.canReadPost(forum.getForumID()) && (forum.getForumStatus() != ForumBean.FORUM_STATUS_DISABLED));
	    row.put("checkForumToSee", checkForumToSee);
	    row.put("name", "forum");

	    if (checkForumToSee) {
	        //this is not a disable forum
	        String forumIcon = MyUtil.getForumIconName(listForums.onlineUser.getLastLogonTimestamp().getTime(), forum.getForumLastPostDate().getTime(), forum.getForumStatus(), forum.getForumThreadCount());
	        row.put("forumIcon", forumIcon);

	        listForums.forumIconLegend.updateIconLegend(forumIcon);

	        row.put("forumCountInCurrentCategory", new Integer(listForums.forumCountInCurrentCategory));
	        listForums.forumCountInCurrentCategory++;

	        row.put("ContextPath", listForums.request.getContextPath());

	        StringBuffer filter = new StringBuffer();
	        filter.append(MyUtil.filter(forum.getForumDesc(), false/*html*/, true/*emotion*/, true/*mvnCode*/, true/*newLine*/, true/*URL*/));
	        row.put("filter", filter.toString());

	        row.put("ForumThreadCount", String.valueOf(forum.getForumThreadCount()));
	        row.put("ForumPostCount", String.valueOf(forum.getForumPostCount()));

	        String listThreadsLink = listForums.urlResolver.encodeURL(listForums.request, listForums.response, "listthreads?forum=" + forum.getForumID());
	        row.put("ForumName", forum.getForumName());
	        row.put("ListThreadsLink", listThreadsLink);

	        row.put("no_post", MVNForumResourceBundle.getString(listForums.locale, "mvnforum.user.listforums.table.no_post"));
	        row.put("by", MVNForumResourceBundle.getString(listForums.locale, "mvnforum.common.by"));

	        boolean checkCondition = (forum.getLastPostMemberName().length() == 0) || (forum.getForumThreadCount() == 0);
	        row.put("checkCondition", checkCondition);

	        row.put("GMTTimestampFormat", listForums.onlineUser.getGMTTimestampFormat(forum.getForumLastPostDate()));

	        String viewmemberLink = listForums.urlResolver.encodeURL(listForums.request, listForums.response, "viewmember?member=" + Encoder.encodeURL(forum.getLastPostMemberName()));
	        row.put("viewmemberLink", viewmemberLink);

	        row.put("LastPostMemberName", forum.getLastPostMemberName());

	        row.put("jsforumprefix", "__" + getCategoryID() + "_f" + forum.getForumID());
	    }

	    listForums.rows.add(row);

	    return "";
	}

} //end of class CategoryBean

