	public String drawForum2(RSSImpl rssImpl) {

	    SimpleHash subRoot = new SimpleHash();
	    ForumBean forum = (ForumBean) getSource();

	    subRoot.put("forumName", forum.getForumName());

	    int forumID = forum.getForumID();
	    subRoot.put("forumID", String.valueOf(forumID));

	    subRoot.put("ContextPath", rssImpl.request.getContextPath());

	    if (forum.getCategoryID() != rssImpl.categoryID) {
	        return "";
	    }

	    boolean check_permission = rssImpl.permission.canReadPost(forumID) && (forum.getForumStatus() != ForumBean.FORUM_STATUS_DISABLED);
	    subRoot.put("check_permission", check_permission);

	    subRoot.put("forumCountInCurrentCategory", new Integer(RSSImpl.rowIndex));
	    RSSImpl.rowIndex++;

	    subRoot.put("name", "forum");
	    subRoot.put("urlRSS091", RSSImpl.urlResolver.encodeURL(rssImpl.request, rssImpl.response, "rss?forum=" + forumID + "&amp;sort=" + rssImpl.sort + "&amp;order=" + rssImpl.order, URLResolverService.ACTION_URL));
	    subRoot.put("urlRSS20", RSSImpl.urlResolver.encodeURL(rssImpl.request, rssImpl.response, "rss2?forum=" + forumID + "&amp;sort=" + rssImpl.sort + "&amp;order=" + rssImpl.order, URLResolverService.ACTION_URL));
	    subRoot.put("urlATOM", RSSImpl.urlResolver.encodeURL(rssImpl.request, rssImpl.response, "atom?forum=" + forumID + "&amp;sort=" + rssImpl.sort + "&amp;order=" + rssImpl.order, URLResolverService.ACTION_URL));
	    subRoot.put("forum_specific_feed", MVNForumResourceBundle.getString(rssImpl.locale, "mvnforum.common.forum_specific_feed"));

	    rssImpl.rows.add(subRoot);

	    return "";
	}
