        hash = 71 * hash + Objects.hashCode(this.errorIndicatorPaint);
        hash = 71 * hash + Objects.hashCode(this.gridBandPaint);
        hash = 71 * hash + Objects.hashCode(this.gridBandAlternatePaint);
        return hash;
    }

